import React, { useEffect, useState } from 'react';
import GameList from './components/GameList';
import GameForm from './components/GameForm';
import Stats from './components/Stats';
import api from './utils/api';

export default function App(){
  const [games, setGames] = useState([]);
  const [query, setQuery] = useState('');
  const [filter, setFilter] = useState({});

  const load = async () => {
    const q = query ? `?q=${encodeURIComponent(query)}` : '';
    const res = await api.get('/games'+q);
    setGames(res);
  };

  useEffect(()=>{ load(); }, []);

  return (
    <div className='app'>
      <header className='topbar'>
        <h1>Mi Biblioteca Gaming</h1>
        <div className='controls'>
          <input placeholder='Buscar juegos...' value={query} onChange={e=>setQuery(e.target.value)} />
          <button onClick={load}>Buscar</button>
        </div>
      </header>
      <main className='main'>
        <aside className='sidebar'>
          <GameForm onCreate={g=> setGames(prev=>[g,...prev])} />
          <Stats games={games} />
        </aside>
        <section className='content'>
          <GameList games={games} setGames={setGames} />
        </section>
      </main>
    </div>
  );
}
