import ReviewList from './ReviewList'
import React, { useState }  from 'react';
import api from '../utils/api';
import ReviewForm from './ReviewForm';
import './Modal.css';

export default function GameCard({game, setGames}){
  const [showModal, setShowModal] = useState(false); // üîπ controla si el modal se muestra o no
  const [showReviews, setShowReviews] = useState(false);

  const toggleCompleted = async () => {
    const updated = await api.put('/games/'+game._id, {...game, completed: !game.completed});
    setGames(prev => prev.map(p => p._id === updated._id ? updated : p));
  };

  const remove = async () => {
    if (!confirm('Eliminar '+game.title+'?')) return;
    await api.delete('/games/'+game._id);
    setGames(prev => prev.filter(p => p._id !== game._id));
  };

  return (
    <div className='card'>
      <div className='cover' style={{backgroundImage: `url(${game.coverUrl || ''})`}} />
      <div className='info'>
        <h3>{game.title}</h3>
        <p className='meta'>{game.genre} ‚Ä¢ {game.platform}</p>
        <p>Horas jugadas: {game.hoursPlayed || 0}</p>

        <div className='actions'>
          <button onClick={toggleCompleted}>
            {game.completed ? 'Desmarcar completado' : 'Marcar como completado'}
          </button>
          <button onClick={remove}>Eliminar</button>
           <button className='open-reviews-btn' onClick={() => setShowReviews(true)}>
            Abrir rese√±as
           </button>
        </div>


        <div className='reviews'>
        <h4>Rese√±as</h4>
        <ReviewList gameId={game._id} />

      {/* <ReviewForm gameId={game._id} onCreate={(newReview) => { 
        console.log('Nueva rese√±a:', newReview);
      }
      // Para actualizar las rese√±as despu√©s de crear una
      // Podr√≠as recargar el componente ReviewList si quieres
    }/> */}

      </div>
      </div>

    </div>
  );
}
