import React, { useState } from 'react';
import api from '../utils/api';
import './ReviewForm.css';

export default function ReviewForm ({ gameId, onCreate }) {
  const [title, setTitle] = useState('');
  const [comment, setComment] = useState('');
  const [rating, setRating] = useState(0);
  const [hover, setHover] = useState(0);

  const submit = async (e) => {
    e.preventDefault();
    onCreate({ title, content, rating, gameId });
    if (!title || !comment) {
    return alert('Completa todos los campos');
    }

    try {
      const response = await api.post(`/games/${gameId}/reviews`, {
        title,
        comment,
        rating,
      });

      setTitle('');
      setComment('');
      setRating(0);

      if (onCreate) onCreate(response.data);  // envía la nueva reseña al padre
      if (onClose) onClose(); // cierra modal
    } catch (error) {
      console.error(error);
      alert('Error al enviar la reseña');
    }
  };

  return (
    <form onSubmit={submit} className='review-form'>
      <input
        placeholder='Título' req
        value={title}
        onChange={e => setTitle(e.target.value)}
      />
      <textarea
        placeholder='Escribe tu reseña...'
        value={comment}
        onChange={e => setComment(e.target.value)}
        placeholder="Tu reseña"
        required

      />
      <div className="star-rating">
        {[1, 2, 3, 4, 5].map((star) => (
          <span
            key={star}
            className={`star ${star <= (hover || rating) ? 'filled' : ''}`}
            onClick={() => setRating(star)}
            onMouseEnter={() => setHover(star)}
            onMouseLeave={() => setHover(0)}
          >
            ★
          </span>
        ))}
      </div>

      <div className="buttons">
        <button type="button" onClick={onClose}>Cancelar</button>
        <button type="submit">Guardar reseña</button>
      </div>
    </form>
  );
}
